import { useState } from "react";
import * as XLSX from "xlsx";
import API from "../utils/api";

export default function ProcessFile() {
  const [data, setData] = useState([]);
  const [column, setColumn] = useState("");
  const [formula, setFormula] = useState("");
  const [lookupValue, setLookupValue] = useState("");
  const [result, setResult] = useState(null);

  const upload = (e) => {
    const reader = new FileReader();
    reader.onload = (event) => {
      const wb = XLSX.read(event.target.result, { type: "binary" });
      const ws = wb.Sheets[wb.SheetNames[0]];
      const json = XLSX.utils.sheet_to_json(ws);
      setData(json);
    };
    reader.readAsBinaryString(e.target.files[0]);
  };

  const process = async () => {
    const res = await API.post("/file/process", {
      formula,
      column,
      lookupValue,
      fileData: data,
    });
    setResult(res.data.result);
  };

  return (
    <div>
      <h2>Process Excel/CSV File</h2>

      <input type="file" accept=".xlsx,.csv" onChange={upload} />

      <select onChange={(e) => setFormula(e.target.value)}>
        <option>Select Formula</option>
        <option value="sum">SUM</option>
        <option value="vlookup">VLOOKUP</option>
      </select>

      <input
        placeholder="Column Name (A/B/C)"
        onChange={(e) => setColumn(e.target.value)}
      />

      {formula === "vlookup" && (
        <input
          placeholder="Lookup value"
          onChange={(e) => setLookupValue(e.target.value)}
        />
      )}

      <button onClick={process}>Apply</button>

      {result && <pre>{JSON.stringify(result, null, 2)}</pre>}
    </div>
  );
}
