import { useState } from "react";
import * as XLSX from "xlsx";
import { API_BASE_URL } from "../config";

export default function ProcessFile() {
  const [fileData, setFileData] = useState([]);
  const [columns, setColumns] = useState([]);
  const [formula, setFormula] = useState("sum");
  const [column, setColumn] = useState("");
  const [lookupValue, setLookupValue] = useState("");
  const [fileName, setFileName] = useState("");
  const [result, setResult] = useState(null);

  // ✔ Handle Excel or CSV Upload
  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setFileName(file.name);

    const reader = new FileReader();
    reader.onload = (evt) => {
      const data = new Uint8Array(evt.target.result);
      const workbook = XLSX.read(data, { type: "array" });

      const sheet = workbook.SheetNames[0];
      const worksheet = XLSX.utils.sheet_to_json(workbook.Sheets[sheet]);

      setFileData(worksheet);

      if (worksheet.length > 0) {
        const colNames = Object.keys(worksheet[0]);
        setColumns(colNames);
      }
    };

    reader.readAsArrayBuffer(file);
  };

  // ✔ Apply Formula
  const applyFormula = async () => {
    if (formula !== "count" && !column) return alert("Please select a column!");

    const response = await fetch(`${API_BASE_URL}/file/process`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
      body: JSON.stringify({
        formula,
        column,
        lookupValue,
        fileName,
        fileData,
      }),
    });

    const data = await response.json();
    setResult(data.result);
  };

  return (
    <div className="flex justify-center p-6">
      <div className="w-full max-w-3xl bg-white shadow-lg p-6 rounded-lg">
        <h2 className="text-2xl font-bold mb-4">Upload & Process Excel File</h2>

        {/* ✔ File Upload */}
        <input type="file" onChange={handleFileUpload} className="mb-3" />

        {/* ✔ Formula Dropdown */}
        <select
          className="border p-2 rounded w-full mb-3"
          value={formula}
          onChange={(e) => setFormula(e.target.value)}
        >
          <option value="sum">SUM</option>
          <option value="average">AVERAGE</option>
          <option value="max">MAX</option>
          <option value="min">MIN</option>
          <option value="count">COUNT</option>
          <option value="countif">COUNTIF</option>
          <option value="vlookup">VLOOKUP</option>
          <option value="match">MATCH</option>
          <option value="cell">CELL VALUE</option>
        </select>

        {/* ✔ Column Dropdown */}
        {formula !== "count" && (
          <select
            className="border p-2 rounded w-full mb-3"
            value={column}
            onChange={(e) => setColumn(e.target.value)}
          >
            <option value="">Select Column</option>
            {columns.map((col, idx) => (
              <option key={idx} value={col}>
                {col}
              </option>
            ))}
          </select>
        )}

        {/* ✔ VLOOKUP & MATCH input */}
        {(formula === "vlookup" || formula === "match") && (
          <input
            type="text"
            placeholder="Enter lookup value"
            className="border p-2 w-full mb-3"
            value={lookupValue}
            onChange={(e) => setLookupValue(e.target.value)}
          />
        )}

        {/* ✔ COUNTIF condition */}
        {formula === "countif" && (
          <input
            type="text"
            placeholder="Enter condition (e.g. >50)"
            className="border p-2 w-full mb-3"
            value={lookupValue}
            onChange={(e) => setLookupValue(e.target.value)}
          />
        )}

        {/* ✔ CELL formula row number */}
        {formula === "cell" && (
          <input
            type="number"
            placeholder="Enter row number (0 = first row)"
            className="border p-2 w-full mb-3"
            value={lookupValue}
            onChange={(e) => setLookupValue(e.target.value)}
          />
        )}

        {/* ✔ Apply Formula Button */}
        <button
          onClick={applyFormula}
          className="bg-blue-600 text-white w-full py-3 rounded mt-2 hover:bg-blue-700"
        >
          Apply Formula
        </button>

        {/* ✔ Result Panel */}
        {result !== null && (
          <div className="mt-6 p-4 bg-gray-100 rounded">
            <h3 className="text-xl font-semibold mb-2">Result:</h3>
            <pre className="bg-white p-3 rounded shadow overflow-x-auto">
              {typeof result === "object"
                ? JSON.stringify(result, null, 2)
                : result}
            </pre>
          </div>
        )}

        {/* ✔ Preview Table */}
        {fileData.length > 0 && (
          <div className="mt-6">
            <h3 className="text-lg font-semibold mb-2">Preview</h3>

            <table className="w-full border">
              <thead className="bg-gray-200">
                <tr>
                  {columns.map((col, idx) => (
                    <th key={idx} className="border p-2">
                      {col}
                    </th>
                  ))}
                </tr>
              </thead>

              <tbody>
                {fileData.map((row, index) => (
                  <tr key={index}>
                    {columns.map((col, idx) => (
                      <td key={idx} className="border p-2">
                        {row[col]}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}
