import { useState } from "react";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import API from "../utils/api";
import Navbar from "../components/Navbar";

export default function ProcessFile() {
  const [data, setData] = useState([]);
  const [formula, setFormula] = useState("");
  const [column, setColumn] = useState("");
  const [lookupValue, setLookupValue] = useState("");
  const [result, setResult] = useState(null);
  const [fileName, setFileName] = useState("");

  const uploadFile = (e) => {
    const file = e.target.files[0];
    setFileName(file.name);

    const reader = new FileReader();
    reader.onload = (event) => {
      const wb = XLSX.read(event.target.result, { type: "binary" });
      const ws = wb.Sheets[wb.SheetNames[0]];
      const json = XLSX.utils.sheet_to_json(ws);
      setData(json);
    };
    reader.readAsBinaryString(file);
  };

  const applyFormula = async () => {
    const res = await API.post("/file/process", {
      formula,
      column,
      lookupValue,
      fileName,
      fileData: data,
    });
    setResult(res.data.result);
  };

  const downloadExcel = () => {
    const ws = XLSX.utils.json_to_sheet([result]);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Result");

    const buffer = XLSX.write(wb, { bookType: "xlsx", type: "array" });
    const blob = new Blob([buffer], { type: "application/octet-stream" });
    saveAs(blob, "processed_result.xlsx");
  };

  return (
    <>
      <Navbar />

      <div className="max-w-4xl mx-auto mt-10 bg-white shadow-lg p-8 rounded">
        <h2 className="text-2xl font-bold mb-6">Upload & Process Excel File</h2>

        <input
          type="file"
          className="w-full border p-2 mb-4"
          onChange={uploadFile}
        />

        <select
          className="w-full border p-2 rounded mb-4"
          onChange={(e) => setFormula(e.target.value)}
        >
          <option>Select Formula</option>
          <option value="sum">SUM</option>
          <option value="vlookup">VLOOKUP</option>
        </select>

        <input
          className="w-full border p-2 rounded mb-4"
          placeholder="Column (A/B/C)"
          onChange={(e) => setColumn(e.target.value)}
        />

        {formula === "vlookup" && (
          <input
            className="w-full border p-2 rounded mb-4"
            placeholder="Lookup Value"
            onChange={(e) => setLookupValue(e.target.value)}
          />
        )}

        <button
          onClick={applyFormula}
          className="w-full bg-blue-600 text-white py-3 rounded mb-6"
        >
          Apply Formula
        </button>

        {data.length > 0 && (
          <>
            <h3 className="text-xl font-semibold mb-2">Preview</h3>
            <div className="overflow-auto max-h-60 border rounded">
              <table className="min-w-full text-sm">
                <thead className="bg-gray-200">
                  <tr>
                    {Object.keys(data[0]).map((col) => (
                      <th key={col} className="p-2 border">
                        {col}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {data.map((row, i) => (
                    <tr key={i}>
                      {Object.values(row).map((val, j) => (
                        <td className="p-2 border" key={j}>
                          {val}
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </>
        )}

        {result && (
          <>
            <h3 className="text-xl font-semibold mt-6 mb-2">
              Processed Result
            </h3>
            <pre className="bg-gray-100 p-4 border rounded">
              {JSON.stringify(result, null, 2)}
            </pre>

            <button
              onClick={downloadExcel}
              className="w-full mt-4 bg-green-600 text-white py-3 rounded"
            >
              Download Excel
            </button>
          </>
        )}
      </div>
    </>
  );
}
